mapper.select:
select 

from LOGGING_EVENT_PROPERTY
where EVENT_ID = #{eventId,jdbcType=DECIMAL}
  and MAPPED_KEY = #{mappedKey,jdbcType=VARCHAR}

mapper.delete:
delete from LOGGING_EVENT_PROPERTY
where EVENT_ID = #{eventId,jdbcType=DECIMAL}
  and MAPPED_KEY = #{mappedKey,jdbcType=VARCHAR}

mapper.insert:
insert into LOGGING_EVENT_PROPERTY (EVENT_ID, MAPPED_KEY, MAPPED_VALUE
  )
values (#{eventId,jdbcType=DECIMAL}, #{mappedKey,jdbcType=VARCHAR}, #{mappedValue,jdbcType=VARCHAR}
  )

mapper.update:
update LOGGING_EVENT_PROPERTY


    MAPPED_VALUE = #{mappedValue,jdbcType=VARCHAR},


where EVENT_ID = #{eventId,jdbcType=DECIMAL}
  and MAPPED_KEY = #{mappedKey,jdbcType=VARCHAR}

update LOGGING_EVENT_PROPERTY
set MAPPED_VALUE = #{mappedValue,jdbcType=VARCHAR}
where EVENT_ID = #{eventId,jdbcType=DECIMAL}
  and MAPPED_KEY = #{mappedKey,jdbcType=VARCHAR}
